<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of OfferPhoto * * @author lessons */class Lessons {    public $id;    public $class_id;    public $title;    public $image_name;    public $payment_method;    public $class_fee;    public $video_duration;    public $description;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT *  FROM `lessons` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->class_id = $result['class_id'];            $this->title = $result['title'];            $this->image_name = $result['image_name'];            $this->payment_method = $result['payment_method'];            $this->class_fee = $result['class_fee'];            $this->video_duration = $result['video_duration'];            $this->description = $result['description'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `lessons` (`class_id`,`title`,`image_name`,`description`,`payment_method`,`class_fee`,`video_duration`,`queue`) VALUES  ('"                . $this->class_id . "','"                . $this->title . "','"                . $this->image_name . "', '"                . $this->description . "', '"                . $this->payment_method . "', '"                . $this->class_fee . "', '"                . $this->video_duration . "', '"                . $this->queue . "')";                    $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `lessons` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `lessons` SET "                 . "`title` ='" . $this->title . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`description` ='" . $this->description . "', "                . "`payment_method` ='" . $this->payment_method . "', "                . "`class_fee` ='" . $this->class_fee . "', "                . "`video_duration` ='" . $this->video_duration . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";                $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `lessons` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function getLessonsByClsass($class_id) {        $query = "SELECT * FROM `lessons` WHERE `class_id`= $class_id  ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `lessons` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}