$(".wizard-horizontal").steps({
    headerTag: "h6",
    bodyTag: "fieldset",
    transitionEffect: "fade",
    titleTemplate: '<span class="step">#index#</span> #title#',
    labels: {
        finish: "Submit"
    },
    onFinished: function (i, e) {

        tinymce.triggerSave();
        if (!$('#type').val() || $('#type').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your property type..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#title').val() || $('#title').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your Property name..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#address_1').val() || $('#address_1').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your Street address..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#address_2').val() || $('#address_2').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your Address Line 2..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#city').val() || $('#city').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your city..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#contact_name ').val() || $('#contact_name').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your contact name..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#mobile_number_1 ').val() || $('#mobile_number_1').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your contact number..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#description').val() || $('#description').val().length === 0) {
            swal({
                title: "Error!",
                text: "Please enter your Property description..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else if (!$('#agree').is(':checked')) {
            swal({
                title: "Error!",
                text: "Please accept our agrement..!",
                type: 'error',
                timer: 1500,
                showConfirmButton: false
            });
        } else {

             //start preloarder
            $('.someBlock').preloader();


            var formData = new FormData($('#form-data')[0]);
            $.ajax({
                url: "post-and-get/ajax/accommodation.php",
                type: "POST",
                data: formData,
                async: false,
                dataType: 'json',
                success: function (result) {

                     //remove preloarder
                    $('.someBlock').preloader('remove');
                    
                    $('.uploard_btn').show();
                    swal({
                        title: "Success!",
                        text: "Your changes saved successfully!...",
                        type: 'success',
                        timer: 2000,
                        showConfirmButton: false
                    }, function () {
                        setTimeout(function () {
                            window.location.replace("create-room.php?id=" + result.id + "&&message=19");
                        }, 1500);
                    });
                },
                cache: false,
                contentType: false,
                processData: false
            });
        }
    }
}), $(".wizard-vertical").steps({
    headerTag: "h3",
    bodyTag: "fieldset",
    transitionEffect: "fade",
    enableAllSteps: !0,
    stepsOrientation: "vertical",
    labels: {
        finish: "Submit"
    },
    onFinished: function (i, e) {
        alert("Form submitted.")
    }
});
var stepsValidation = $(".wizard-validation"),
        form = stepsValidation.show();
stepsValidation.steps({
    headerTag: "h6",
    bodyTag: "fieldset",
    transitionEffect: "fade",
    titleTemplate: '<span class="step">#index#</span> #title#',
    labels: {
        finish: "Submit"
    },
    onStepChanging: function (i, e, n) {
        return n < e || (form.validate().settings.ignore = ":disabled,:hidden", form.valid())
    },
    onFinishing: function (i, e) {
        return form.validate().settings.ignore = ":disabled", form.valid()
    },
    onFinished: function (i, e) {
        alert("Submitted!")
    }
}), stepsValidation.validate({
    ignore: "input[type=hidden]",
    errorClass: "danger",
    successClass: "success",
    highlight: function (i, e) {
        $(i).removeClass(e)
    },
    unhighlight: function (i, e) {
        $(i).removeClass(e)
    },
    errorPlacement: function (i, e) {
        i.insertAfter(e)
    },
    rules: {
        email: {
            email: !0
        }
    }
}), $(document).ready(function () {
    $(".current").find(".step-icon").addClass("bx bx-time-five"), $(".current").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#5A8DEE"
    })
}), $(".actions [href='#next']").click(function () {
    $(".done").find(".step-icon").removeClass("bx bx-time-five").addClass("bx bx-check-circle"), $(".current").find(".step-icon").removeClass("bx bx-check-circle").addClass("bx bx-time-five"), $(".current").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#5A8DEE"
    }), $(".current").prev("li").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#39DA8A"
    })
}), $(".actions [href='#previous']").click(function () {
    $(".current").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#5A8DEE"
    }), $(".current").next("li").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#adb5bd"
    })
}), $(".actions [href='#finish']").click(function () {
    $(".done").find(".step-icon").removeClass("bx-time-five").addClass("bx bx-check-circle"), $(".last.current.done").find(".fonticon-wrap .livicon-evo").updateLiviconEvo({
        strokeColor: "#39DA8A"
    })
}), $('.actions a[role="menuitem"]').addClass("btn btn-primary"), $('.icon-tab [role="menuitem"]').addClass("glow "), $('.wizard-vertical [role="menuitem"]').removeClass("btn-primary").addClass("btn-light-primary");